import React, { useState, useEffect } from "react";
import { BrowserRouter as Router, Route, Link, Routes, useParams, useNavigate } from "react-router-dom";
import { Heart, ShoppingCart, Trash, ArrowLeft } from "lucide-react";

function HomeButton() {
  const navigate = useNavigate();
  return (
    <button onClick={() => navigate("/")} className="mt-4 bg-gray-500 text-white px-4 py-2 rounded flex items-center">
      <ArrowLeft className="mr-2" size={16} /> العودة إلى الصفحة الرئيسية
    </button>
  );
}

function ProductList({ addToCart, toggleFavorite, favorites }) {
  const [products, setProducts] = useState([]);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    fetch("https://fakestoreapi.com/products")
      .then((res) => res.json())
      .then((data) => {
        setProducts(data.slice(0, data.length - 2));
        setLoading(false);
      });
  }, []);

  if (loading) return <p>جاري التحميل...</p>;

  return (
    <div className="p-4 grid grid-cols-1 md:grid-cols-3 gap-4">
      {products.map((product) => (
        <div key={product.id} className="p-4 shadow-lg rounded-lg text-center border hover:scale-105 transition-transform">
          <img src={product.image} alt={product.title} className="w-full h-56 object-contain bg-transparent mb-2" />
          <h2 className="text-lg font-bold">{product.title}</h2>
          <p className="text-gray-600">${product.price}</p>
          <div className="flex justify-between mt-2">
            <Link to={`/product/${product.id}`} className="text-blue-500 underline">عرض التفاصيل</Link>
            <button onClick={() => addToCart(product)} className="bg-blue-500 text-white px-3 py-1 rounded flex items-center">
              <ShoppingCart className="mr-2" size={16} /> أضف إلى السلة
            </button>
            <button onClick={() => toggleFavorite(product)} className="bg-gray-200 p-1 rounded">
              <Heart className={favorites.some(p => p.id === product.id) ? "text-red-500" : "text-gray-400"} size={20} />
            </button>
          </div>
        </div>
      ))}
    </div>
  );
}

function ProductDetails() {
  const { id } = useParams();
  const [product, setProduct] = useState(null);
  useEffect(() => {
    fetch(`https://fakestoreapi.com/products/${id}`)
      .then((res) => res.json())
      .then((data) => setProduct(data));
  }, [id]);

  if (!product) return <p>جاري التحميل...</p>;

  return (
    <div className="p-4 text-center">
      <img src={product.image} alt={product.title} className="w-64 h-64 object-contain bg-transparent mx-auto" />
      <h2 className="text-2xl font-bold mt-2">{product.title}</h2>
      <p className="text-gray-600">{product.description}</p>
      <p className="text-lg font-bold mt-2">${product.price}</p>
      <HomeButton />
    </div>
  );
}

function Cart({ cart, removeFromCart }) {
  return (
    <div className="p-4">
      <h2 className="text-xl font-bold mb-4">سلة التسوق</h2>
      {cart.length === 0 ? <p>سلتك فارغة.</p> : cart.map((product) => (
        <div key={product.id} className="flex justify-between items-center border p-4 mb-2">
          <img src={product.image} alt={product.title} className="w-16 h-16 object-contain" />
          <p>{product.title}</p>
          <p>${product.price}</p>
          <button onClick={() => removeFromCart(product)} className="text-red-500">
            <Trash size={20} />
          </button>
        </div>
      ))}
      <HomeButton />
    </div>
  );
}

function Favorites({ favorites, removeFromFavorites, moveToCart }) {
  return (
    <div className="p-4">
      <h2 className="text-xl font-bold mb-4">المفضلة</h2>
      {favorites.length === 0 ? <p>قائمتك فارغة.</p> : favorites.map((product) => (
        <div key={product.id} className="flex justify-between items-center border p-4 mb-2">
          <img src={product.image} alt={product.title} className="w-16 h-16 object-contain" />
          <p>{product.title}</p>
          <p>${product.price}</p>
          <button onClick={() => moveToCart(product)} className="bg-blue-500 text-white px-3 py-1 rounded">شراء الآن</button>
          <button onClick={() => removeFromFavorites(product)} className="text-red-500">
            <Trash size={20} />
          </button>
        </div>
      ))}
      <HomeButton />
    </div>
  );
}

export default function ECommerce() {
  const [cart, setCart] = useState([]);
  const [favorites, setFavorites] = useState([]);

  const addToCart = (product) => {
    if (!cart.some((p) => p.id === product.id)) {
      setCart([...cart, product]);
    }
  };

  const removeFromCart = (product) => setCart(cart.filter((p) => p.id !== product.id));
  const toggleFavorite = (product) => {
    setFavorites(favorites.some((p) => p.id === product.id) ? favorites.filter((p) => p.id !== product.id) : [...favorites, product]);
  };
  const removeFromFavorites = (product) => setFavorites(favorites.filter((p) => p.id !== product.id));
  const moveToCart = (product) => { removeFromFavorites(product); addToCart(product); };

  return (
    <Router>
      <Routes>
        <Route path="/cart" element={<Cart cart={cart} removeFromCart={removeFromCart} />} />
        <Route path="/favorites" element={<Favorites favorites={favorites} removeFromFavorites={removeFromFavorites} moveToCart={moveToCart} />} />
        <Route path="/product/:id" element={<ProductDetails />} />
        <Route path="/" element={<ProductList addToCart={addToCart} toggleFavorite={toggleFavorite} favorites={favorites} />} />
      </Routes>
    </Router>
  );
}
