import React, { useState, useEffect } from "react";
import { BrowserRouter as Router, Route, Link, Routes, useParams } from "react-router-dom";
import { Heart, ShoppingCart } from "lucide-react";

function ProductList({ addToCart, toggleFavorite, favorites }) {
  const [products, setProducts] = useState([]);

  useEffect(() => {
    fetch("https://fakestoreapi.com/products")
      .then((res) => res.json())
      .then((data) => {
        setProducts(data.slice(0, data.length - 2)); // Removing two products
      });
  }, []);

  return (
    <div className="p-4 grid grid-cols-1 md:grid-cols-3 gap-4">
      {products.map((product) => (
        <div key={product.id} className="p-4 shadow-lg rounded-lg text-center border hover:scale-105 transition-transform">
          <img src={product.image} alt={product.title} className="w-full h-56 object-contain mb-2" />
          <div>
            <h2 className="text-lg font-bold">{product.title}</h2>
            <p className="text-gray-600">${product.price}</p>
            <div className="flex justify-between mt-2">
              <Link to={`/product/${product.id}`} className="text-blue-500 underline">View Details</Link>
              <button onClick={() => addToCart(product)} className="bg-blue-500 text-white px-3 py-1 rounded flex items-center">
                <ShoppingCart className="mr-2" size={16} /> Add to Cart
              </button>
              <button onClick={() => toggleFavorite(product)} className="bg-gray-200 p-1 rounded">
                <Heart className={favorites.some(p => p.id === product.id) ? "text-red-500" : "text-gray-400"} size={20} />
              </button>
            </div>
          </div>
        </div>
      ))}
    </div>
  );
}

function ProductDetails() {
  const { id } = useParams();
  const [product, setProduct] = useState(null);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    setLoading(true);
    fetch(`https://fakestoreapi.com/products/${id}`)
      .then((res) => res.json())
      .then((data) => {
        setProduct(data);
        setLoading(false);
      });
  }, [id]);

  if (loading) return <p className="text-center">Loading...</p>;
  if (!product) return <p className="text-center text-red-500">Product not found.</p>;

  return (
    <div className="p-4 text-center">
      <img src={product.image} alt={product.title} className="w-1/2 mx-auto mb-4 animate-fade-in" />
      <h1 className="text-2xl font-bold">{product.title}</h1>
      <p className="text-gray-600">{product.description}</p>
      <p className="text-lg text-green-600">Price: ${product.price}</p>
      <Link to="/" className="text-blue-500 underline">Back to Store</Link>
    </div>
  );
}

function Cart({ cart }) {
  return (
    <div className="p-4">
      <h2 className="text-2xl font-bold mb-4">Shopping Cart</h2>
      {cart.length === 0 ? (
        <p className="text-gray-500">Your cart is empty.</p>
      ) : (
        <ul>
          {cart.map((item, index) => (
            <li key={index} className="border p-2 mb-2">{item.title} - ${item.price}</li>
          ))}
        </ul>
      )}
      <Link to="/" className="text-blue-500 underline">Back to Store</Link>
    </div>
  );
}

function Favorites({ favorites }) {
  return (
    <div className="p-4">
      <h2 className="text-2xl font-bold mb-4">Favorite Products</h2>
      {favorites.length === 0 ? (
        <p className="text-gray-500">You have no favorite products.</p>
      ) : (
        <ul>
          {favorites.map((item, index) => (
            <li key={index} className="border p-2 mb-2">{item.title} - ${item.price}</li>
          ))}
        </ul>
      )}
      <Link to="/" className="text-blue-500 underline">Back to Store</Link>
    </div>
  );
}

export default function ECommerce() {
  const [cart, setCart] = useState([]);
  const [favorites, setFavorites] = useState([]);

  const addToCart = (product) => {
    setCart((prevCart) => [...prevCart, product]);
  };

  const toggleFavorite = (product) => {
    setFavorites((prevFavorites) =>
      prevFavorites.some(p => p.id === product.id)
        ? prevFavorites.filter((p) => p.id !== product.id)
        : [...prevFavorites, product]
    );
  };

  return (
    <Router>
      <div className="p-4 flex justify-between items-center bg-gray-100">
        <h1 className="text-xl font-bold">E-Commerce Store</h1>
        <div className="flex space-x-4">
          <Link to="/favorites" className="relative">
            <Heart className="w-8 h-8 text-gray-700" />
            {favorites.length > 0 && (
              <span className="absolute top-0 right-0 bg-red-500 text-white text-xs rounded-full px-2 py-1">
                {favorites.length}
              </span>
            )}
          </Link>
          <Link to="/cart" className="relative">
            <ShoppingCart className="w-8 h-8 text-gray-700" />
            {cart.length > 0 && (
              <span className="absolute top-0 right-0 bg-red-500 text-white text-xs rounded-full px-2 py-1">
                {cart.length}
              </span>
            )}
          </Link>
        </div>
      </div>
      <Routes>
        <Route path="/product/:id" element={<ProductDetails />} />
        <Route path="/cart" element={<Cart cart={cart} />} />
        <Route path="/favorites" element={<Favorites favorites={favorites} />} />
        <Route path="/" element={<ProductList addToCart={addToCart} toggleFavorite={toggleFavorite} favorites={favorites} />} />
      </Routes>
    </Router>
  );
}
